schemas:
DEFINE TABLE author SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD id ON author TYPE record<author> ASSERT $value != NONE;
DEFINE FIELD name ON author TYPE string ASSERT $value != NONE;
DEFINE FIELD bio ON author TYPE option<string>;
DEFINE FIELD email ON author TYPE string ASSERT string::is::email($value);
DEFINE FIELD linkedin ON author TYPE option<string>;
DEFINE FIELD twitter ON author TYPE option<string>;
DEFINE FIELD github ON author TYPE option<string>;

DEFINE TABLE post SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD id ON post TYPE record<post> ASSERT $value != NONE;
DEFINE FIELD title ON post TYPE string ASSERT $value != NONE;
DEFINE FIELD summary ON post TYPE string ASSERT $value != NONE;
DEFINE FIELD body ON post TYPE string ASSERT $value != NONE;
DEFINE FIELD tags ON post TYPE array<string> ASSERT $value != NONE;
DEFINE FIELD author ON post TYPE record<author> ASSERT $value != NONE;
DEFINE FIELD read_time ON post TYPE int ASSERT $value != NONE;
DEFINE FIELD created_at ON post TYPE datetime DEFAULT time::now();
DEFINE FIELD updated_at ON post TYPE datetime VALUE time::now();
DEFINE TABLE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
